mapper.select:
SELECT id, range_id, stream_id, epoch, start_offset, end_offset, broker_id
FROM `range`
WHERE range_id = #{rangeId}

SELECT id, range_id, stream_id, epoch, start_offset, end_offset, broker_id
FROM `range`
WHERE id = #{id}

SELECT id, range_id, stream_id, epoch, start_offset, end_offset, broker_id
FROM `range`
WHERE 1 = 1

    AND range_id = #{rangeId}


    AND stream_id = #{streamId}
    AND broker_id = #{brokerId}

mapper.delete:
DELETE FROM `range`
WHERE 1 = 1

    AND range_id = #{rangeId}


    AND stream_id = #{streamId}


    AND 1 = 0

mapper.select:
SELECT id, range_id, stream_id, epoch, start_offset, end_offset, broker_id
FROM `range`
WHERE stream_id = #{streamId}

SELECT id, range_id, stream_id, epoch, start_offset, end_offset, broker_id
FROM `range`
WHERE broker_id = #{brokerId}

SELECT id, range_id, stream_id, epoch, start_offset, end_offset, broker_id
FROM `range`
WHERE 1 = 1

    AND broker_id = #{brokerId}


    AND stream_id = #{streamId}


    AND start_offset <= #{offset}
    AND end_offset > #{offset}
