mapper.insert:
INSERT INTO s3object (object_size, stream_id, state)
VALUES (
    #{objectSize},
    #{streamId},
    #{state}
)

mapper.update:
UPDATE s3object
SET marked_for_deletion_timestamp = #{markedForDeletionTimestamp}

    , state = #{state}

WHERE 1 = 1

    AND id = #{id}

UPDATE s3object
SET expired_timestamp = #{expiredTimestamp}

    , state = #{state}

WHERE 1 = 1

    AND id = #{id}

mapper.insert:
INSERT INTO s3object (prepared_timestamp, expired_timestamp, state

    , object_size


    , stream_id

)

VALUES (
#{preparedTimestamp},
#{expiredTimestamp},
#{state}

    , #{objectSize}


    , #{streamId}

)

mapper.update:
UPDATE s3object
SET committed_timestamp = #{committedTimestamp}

    , state = #{state}

WHERE 1 = 1

    AND id = #{id}

mapper.select:
SELECT id, object_id, stream_id, object_size, prepared_timestamp, committed_timestamp, expired_timestamp,
marked_for_deletion_timestamp, state
FROM s3object
WHERE 1 = 1

    AND state = #{state}


    AND expired_timestamp <= #{expiredTimestamp}
