mapper.update:
UPDATE s3streamobject
SET committed_timestamp = #{committedTimestamp_}
WHERE object_id = #{objectId_} and stream_id = #{streamId_}

mapper.delete:
DELETE
FROM s3streamobject
WHERE object_id = #{objectId} and stream_id = #{streamId}

