mapper.update:
UPDATE s3walobject
SET committed_timestamp = #{committedTimestamp}
WHERE 1 = 1

    AND object_id = #{objectId}


    AND node_id = #{nodeId}


     AND sequence_id = #{sequenceId}

mapper.select:
SELECT object_id, object_size, node_id, sequence_id, sub_streams, base_data_timestamp, committed_timestamp
FROM s3walobject
WHERE object_id = #{objectId}

mapper.delete:
DELETE
FROM s3walobject
WHERE 1 = 1

    AND object_id = #{objectId}


    AND node_id = #{nodeId}


    AND sequence_id = #{sequenceId}

mapper.select:
SELECT object_id, object_size, node_id, sequence_id, sub_streams, base_data_timestamp, committed_timestamp
FROM s3walobject
WHERE 1 = 1

    AND node_id = #{nodeId}


    AND sequence_id = #{sequenceId}
