mapper.update:
UPDATE stream_affiliation
SET status = #{status}, src_node_id = #{srcNodeId}, dst_node_id = #{dstNodeId}
WHERE 1 = 1

    AND topic_id = #{topicId}


    AND queue_id = #{queueId}


    AND group_id = #{groupId}

mapper.delete:
DELETE FROM stream_affiliation
WHERE 1 = 1

    AND topic_id = #{topicId}


    AND queue_id = #{queueId}

mapper.select:
SELECT topic_id, queue_id, stream_id, stream_role, group_id, src_node_id, dst_node_id, status, create_time, update_time
FROM stream_affiliation
WHERE 1 = 1

    AND topic_id = #{topicId}


    AND queue_id = #{queueId}


    AND group_id = #{groupId}


    AND update_time >= #{updateTime}
