mapper.update:
UPDATE stream
SET epoch = epoch + 1
WHERE id = #{id}

UPDATE stream
SET range_id = #{lastRangeId}
WHERE id = #{id}

UPDATE stream
epoch = #{epoch}, start_offset = #{startOffset}, range_id = #{rangeId}, state = #{state},
WHERE id = #{id}

UPDATE stream
SET state = #{state}



