mapper.select:
SELECT MAX(epoch)
FROM stream
WHERE topic_id = #{topicId} AND queue_id = #{queueId}

SELECT id, topic_id, queue_id, stream_role, group_id, src_node_id, dst_node_id, epoch, range_id, start_offset,
state
FROM stream



            #{item}


    AND topic_id = #{criteria.topicId}
    AND queue_id = #{criteria.queueId}
    AND state = #{criteria.state}
    AND update_time >= #{criteria.updateTime}
    AND src_node_id = #{criteria.srcNodeId}
    AND dst_node_id = #{criteria.dstNodeId}
    AND group_id = #{criteria.groupId}
