mapper.select:
SELECT id, range_id, stream_id, epoch, start_offset, end_offset, node_id
FROM `range`
WHERE id = #{id}

SELECT id, range_id, stream_id, epoch, start_offset, end_offset, node_id
FROM `range`

    stream_id = #{streamId}
    AND range_id = #{rangeId}
    AND node_id = #{nodeId}

SELECT id, range_id, stream_id, epoch, start_offset, end_offset, node_id
FROM `range`
WHERE stream_id = #{streamId}

SELECT id, range_id, stream_id, epoch, start_offset, end_offset, node_id
FROM `range`
WHERE node_id = #{nodeId}

SELECT id, range_id, stream_id, epoch, start_offset, end_offset, node_id
FROM `range`

    AND node_id = #{nodeId}
    AND stream_id = #{streamId}

        AND start_offset <= #{offset}
        AND end_offset > #{offset}
