mapper.select:
SELECT id, object_id, object_size, stream_id, start_offset, end_offset, base_data_timestamp, committed_timestamp
FROM s3streamobject
WHERE id = #{id}

SELECT id, object_id, object_size, stream_id, start_offset, end_offset, base_data_timestamp, committed_timestamp
FROM s3streamobject
WHERE stream_id = #{streamId}

SELECT id, object_id, object_size, stream_id, start_offset, end_offset, base_data_timestamp, committed_timestamp
FROM s3streamobject
WHERE object_id = #{objectId}

SELECT id, object_id, object_size, stream_id, start_offset, end_offset, base_data_timestamp, committed_timestamp
FROM s3streamobject
WHERE object_id = #{objectId}

mapper.delete:
DELETE
FROM s3streamobject

    id = #{id}
    AND object_id = #{objectId}
    AND stream_id = #{streamId}

mapper.select:
SELECT id, object_id, object_size, stream_id, start_offset, end_offset, base_data_timestamp, committed_timestamp
FROM s3streamobject
WHERE object_id = #{objectId} and stream_id = #{streamId}

SELECT id, object_id, object_size, stream_id, start_offset, end_offset, base_data_timestamp, committed_timestamp
FROM s3streamobject

    object_id = #{objectId}
    AND stream_id = #{streamId}
    AND start_offset <= #{endOffset}
    AND end_offset > #{startOffset}

ORDER BY start_offset ASC

    LIMIT #{limit}

SELECT id, object_id, object_size, stream_id, start_offset, end_offset, base_data_timestamp, committed_timestamp
FROM s3streamobject
WHERE

    #{streamId}

AND committed_timestamp  <   #{threshold}
