mapper.update:
UPDATE topic
SET status = #{status}
WHERE id = #{id}

UPDATE topic
status = #{status}, name = #{name}, queue_num = #{queueNum}, retention_hours = #{retentionHours}, accept_message_types = #{acceptMessageTypes},
WHERE id = #{id}

mapper.delete:
DELETE FROM topic
WHERE status = #{status}
AND #{updateTime} >= update_time

mapper.select:
SELECT id, name, queue_num, retention_hours, status, create_time, update_time, accept_message_types
FROM topic

    AND id = #{id}
    AND name = #{name}

SELECT id, name, queue_num, retention_hours, status, create_time, update_time, accept_message_types
FROM topic

    AND status = #{status}
    AND update_time >= #{updateTime}
